import React,{useState, useEffect} from "react";
import {View, Text, TextInput, StyleSheet, TouchableOpacity, FlatList} from 'react-native';
import Ionicons from '@expo/vector-icons/Ionicons';
import { LinearGradient } from 'expo-linear-gradient';

export default function Home(){
    state = {
        isVisible: false
      };
      // hide show modal
      function displayModal(show){
        this.setState({isVisible: show})
      }
    //Criar uma lista de objetos Tarefas
    let[tarefa, setTarefa] = useState('');
    let[saudacao, setSaudacao] = useState('');
    let[minhasTarefas, setMeusTarefas] = useState([
        {
            id: 1,
            nome: 'tarefa 1',
            descricao:'desc tarefa 1'
        },
        {
            id: 2,
            nome: 'tarefa 2',
            descricao:'desc tarefa 1'
        },
        {
            id: 3,
            nome: 'tarefa 3',
            descricao:'desc tarefa 1'
        },
    ]);

    useEffect(()=>{
        const currentHour = new Date().getHours();

        if(currentHour < 12){
            setSaudacao("Bom dia!!");
        }else if(currentHour >= 12 && currentHour < 18){
            setSaudacao("Boa tarde!!");
        }else{
            setSaudacao("Boa Noite!!");
        }
    },[])

    //funcão para adicionar um tarefa

    function adicionaTarefa(){
        if(tarefa.trim() != ''){
         const dados = {
             id: String(new Date().getTime()),
             nome: tarefa,
           };
      
           //alert("clicou");
       
           setMeusTarefas((oldState) => [... oldState, dados]);
           setTarefa('');
        }
        else{
            alert('Digite um nome de um tarefa')
        }
      
      }

    function deletarTarefa(index){
        console.log('id tarefa:' + index);
        let novosTarefas = [...minhasTarefas];

        novosTarefas = novosTarefas.filter((it, i)=>{
            if(i != index){
                return true;
            }else{
                return false;
            }
        });

        setMeusTarefas(novosTarefas);
    }

    

    return(
        <View style={styles.container}>

        <Modal
            animationType = {"slide"}
            transparent={false}
            visible={this.state.isVisible}
            onRequestClose={() => {
              Alert.alert('Modal has now been closed.');
            }}>
              <Text>
                  Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
                  Maecenas eget tempus augue, a convallis velit.</Text>
          </Modal>

            <Text style={styles.titulo}>To Do List</Text>

                <TextInput 
                    value={tarefa}
                    returnKeyType="search" 
                    style={styles.campo} 
                    onChangeText={setTarefa} 
                    placeholder="Digite uma tarefa"
                    placeholderTextColor="#FFF" 
                />

            <View style={{justifyContent:'flex-start', alignSelf:'stretch'}}>
                <Text style={styles.subTitulo}>Itens</Text>
            </View>

            <FlatList
                data={minhasTarefas}
                style={{alignSelf:'stretch'}}
                keyExtractor={(item) => item.id}
                renderItem={(({item}) => 
                <LinearGradient
                            start={{ x: 0, y: 0.5 }}
                            end={{ x: 1, y: 0.5 }}
                            colors={['transparent', '#D494FF']}
                            style={styles.botaoGradiente}
                        >
                    <View style={styles.botaoTarefa}>
                        <View style={{flexDirection:'row', justifyContent:'space-between', alignItems:'center', alignSelf:'stretch', width:'100%'}}>
                            <View style={{flexDirection:'column', justifyContent:'space-between', alignSelf: 'stretch'}}>
                                <Text style={styles.textoBotaoTarefa}>{item.nome}</Text>
                                <Text style={styles.subTextoBotaoTarefa}>{item.descricao}</Text>
                            </View>
                            <TouchableOpacity onPress={()=>deletarTarefa(item.id)}>
                                <Ionicons name="md-checkmark-circle" size={32} color="white"/>
                            </TouchableOpacity>
                        </View>
                    </View>
                    </LinearGradient>
                )}
          
            />
            
            <TouchableOpacity style={styles.botao}  onPress={adicionaTarefa}>
                <Text style={styles.textoBotao}>+</Text>
                
            </TouchableOpacity>

            <TouchableOpacity style={styles.botao}  onPress={() => {
                this.displayModal(true);
              }}>
                <Text style={styles.textoBotao}>+</Text>
                
            </TouchableOpacity>
        </View>
    );
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor: '#FFF',
        paddingVertical:70,
        paddingHorizontal:20,
        flexDirection: 'column',
        alignItems: 'center',
    },

    titulo:{
        color: '#1E1E1E',
        fontSize:34,
        fontWeight: 'bold',
        marginBottom: 10,
        justifyContent: "center",
        textAlign:"center"

    },

    subTitulo:{
        color: '#1E1E1E',
        fontSize:20,
        fontWeight: 'bold',
        marginBottom: 10,
    },


    campo:{
        backgroundColor: '#C9C5FC',
        fontSize:18,
        marginTop:30,
        borderRadius:50,
        padding:15,
        width: '100%'
    },

    botao:{
        backgroundColor: '#6300FF',
        height:50,
        width: 50,
        borderRadius: 50,
        alignItems: 'center',
        marginTop: 20,
        marginBottom: 20
    },

    textoBotao:{
        color: '#FFF',
        fontSize: 40,
        fontFamily: 'Trebuchet MS',
        fontWeight: 'bold',
    },

    botaoTarefa:{
        padding:15,
        marginBottom: 10,
        alignSelf: 'stretch',
        width:'100%'
        
    },

    botaoGradiente:{
        padding:15,
        marginBottom: 10,
        borderBottomEndRadius: 30,
        borderTopEndRadius: 30,
        alignSelf: 'stretch',
        width:'100%',
        borderLeftWidth:'10px',
        borderLeftColor: '#AE06F4'

    },

    textoBotaoTarefa:{
        fontSize:22,
        fontWeight: 'bold',
        marginBottom: 5
    },
    subTextoBotaoTarefa:{
        fontSize: 17
    }
    });
