import React,{useState, useEffect} from "react";
import {View, Text, TextInput, StyleSheet, TouchableOpacity, FlatList} from 'react-native';
import Ionicons from '@expo/vector-icons/Ionicons';


export default function Home(){

    //Criar uma lista de objetos Filmes
    let[filme, setFilme] = useState('');
    let[saudacao, setSaudacao] = useState('');
    let[meusFilmes, setMeusFilmes] = useState([
        {
            id: 1,
            nome: 'Vingadores'
        },
        {
            id: 2,
            nome: 'Batman'
        },
        {
            id: 3,
            nome: 'Crep√∫sculo'
        },
    ]);

    useEffect(()=>{
        const currentHour = new Date().getHours();

        if(currentHour < 12){
            setSaudacao("Bom dia!!");
        }else if(currentHour >= 12 && currentHour < 18){
            setSaudacao("Boa tarde!!");
        }else{
            setSaudacao("Boa Noite!!");
        }
    },[])

    //func√£o para adicionar um filme

    function adicionaFilme(){
        if(filme.trim() != ''){
         const dados = {
             id: String(new Date().getTime()),
             nome: filme,
           };
      
           //alert("clicou");
       
           setMeusFilmes((oldState) => [... oldState, dados]);
           setFilme('');
        }
        else{
            alert('Digite um nome de um filme')
        }
      
      }

    function deletarFilme(index){
        console.log('id filme:' + index);
        let novosFilmes = [...meusFilmes];

        novosFilmes = novosFilmes.filter((it, i)=>{
            if(i != index){
                return true;
            }else{
                return false;
            }
        });

        setMeusFilmes(novosFilmes);
    }

    return(
        <View style={styles.container}>

            <Text style={styles.titulo}>To Do List</Text>

            <View Style={styles.viewCampo}>
                <TextInput 
                    value={filme}
                    returnKeyType="search" 
                    style={styles.campo} 
                    onChangeText={setFilme} 
                    placeholder="Digite um filme"
                />

                <Ionicons name="md-checkmark-circle" size={32} color="white"/>

            </View>
            

            <TouchableOpacity style={styles.botao}  onPress={adicionaFilme}>
                <Text style={styles.textoBotao}>Adicionar</Text>
                
            </TouchableOpacity>

            <Text style={styles.titulo}>Meus Filmes</Text>

            <FlatList
                data={meusFilmes}
                keyExtractor={(item) => item.id}
                renderItem={(({item}) => 
                    <View style={styles.botaoFilme}>
                        <View style={{flexDirection:'row', justifyContent:'space-between'}}>
                            <Text style={styles.textoBotaoFilme}>{item.nome}</Text>
                            <TouchableOpacity onPress={()=>deletarFilme(item.id)}>
                                <Ionicons name="md-checkmark-circle" size={32} color="white"/>
                            </TouchableOpacity>
                        </View>
                    </View>
                )}
          
            />

        </View>
    );
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor: '#FFF',
        paddingVertical:70,
        paddingHorizontal:20
    },

    titulo:{
        color: '#1E1E1E',
        fontSize:34,
        fontWeight: 'bold',
        marginBottom: 10,
        justifyContent: "center",
        textAlign:"center"

    },

    subTitulo:{
        color: '#1E1E1E',
        fontSize:15,
    },

    viewCampo:{
        flex:1,
        justifyContent: "space-between"
    },

    campo:{
        backgroundColor: 'blue',
        color: '#FFF',
        fontSize:18,
        marginTop:30,
        borderRadius:7,
        padding:15,
        width: 200
    },

    botao:{
        backgroundColor: '#F2C53D',
        padding: 15,
        borderRadius: 7,
        alignItems: 'center',
        marginTop: 20,
        marginBottom: 20
    },

    textoBotao:{
        color: '#4161BF',
        fontSize:17,
        fontWeight: 'bold'
    },

    botaoFilme:{
        backgroundColor: '#1F1E25',
        padding:15,
        marginBottom: 10
    },

    textoBotaoFilme:{
        color: '#FFF',
        fontSize:22,
        fontWeight: 'bold'
    }
    });
