import React,{useState, useEffect} from "react";
import {View, Text, TextInput, StyleSheet, TouchableOpacity, FlatList} from 'react-native';
import Ionicons from '@expo/vector-icons/Ionicons';
import { LinearGradient } from 'expo-linear-gradient';

export default function Home(){

    //Criar uma lista de objetos Tarefas
    let[filme, setTarefa] = useState('');
    let[saudacao, setSaudacao] = useState('');
    let[minhasTarefas, setMeusTarefas] = useState([
        {
            id: 1,
            nome: 'Vingadores'
        },
        {
            id: 2,
            nome: 'Batman'
        },
        {
            id: 3,
            nome: 'Crepúsculo'
        },
    ]);

    useEffect(()=>{
        const currentHour = new Date().getHours();

        if(currentHour < 12){
            setSaudacao("Bom dia!!");
        }else if(currentHour >= 12 && currentHour < 18){
            setSaudacao("Boa tarde!!");
        }else{
            setSaudacao("Boa Noite!!");
        }
    },[])

    //funcão para adicionar um filme

    function adicionaTarefa(){
        if(filme.trim() != ''){
         const dados = {
             id: String(new Date().getTime()),
             nome: filme,
           };
      
           //alert("clicou");
       
           setMeusTarefas((oldState) => [... oldState, dados]);
           setTarefa('');
        }
        else{
            alert('Digite um nome de um filme')
        }
      
      }

    function deletarTarefa(index){
        console.log('id filme:' + index);
        let novosTarefas = [...minhasTarefas];

        novosTarefas = novosTarefas.filter((it, i)=>{
            if(i != index){
                return true;
            }else{
                return false;
            }
        });

        setMeusTarefas(novosTarefas);
    }

    return(
        <View style={styles.container}>

            <Text style={styles.titulo}>To Do List</Text>

                <TextInput 
                    value={filme}
                    returnKeyType="search" 
                    style={styles.campo} 
                    onChangeText={setTarefa} 
                    placeholder="Digite um filme"
                />
            

            <TouchableOpacity style={styles.botao}  onPress={adicionaTarefa}>
                <Text style={styles.textoBotao}>Adicionar</Text>
                
            </TouchableOpacity>

            <Text style={styles.titulo}>Minhas Tarefas</Text>

            <FlatList
                data={minhasTarefas}
                keyExtractor={(item) => item.id}
                renderItem={(({item}) => 
                    <View style={styles.botaoTarefa}>
                        <LinearGradient
                            start={{ x: 0, y: 0.5 }}
                            end={{ x: 1, y: 0.5 }}
                            colors={['transparent', '#D494FF']}
                            style={styles.botaoTarefa}
                        />
                        <View style={{flexDirection:'row', justifyContent:'space-between'}}>
                            <Text style={styles.textoBotaoTarefa}>{item.nome}</Text>
                            <TouchableOpacity onPress={()=>deletarTarefa(item.id)}>
                                <Ionicons name="md-checkmark-circle" size={32} color="white"/>
                            </TouchableOpacity>
                        </View>
                    </View>
                )}
          
            />

        </View>
    );
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor: '#FFF',
        paddingVertical:70,
        paddingHorizontal:20
    },

    titulo:{
        color: '#1E1E1E',
        fontSize:34,
        fontWeight: 'bold',
        marginBottom: 10,
        justifyContent: "center",
        textAlign:"center"

    },

    subTitulo:{
        color: '#1E1E1E',
        fontSize:15,
    },


    campo:{
        backgroundColor: '#FFF',
        fontSize:18,
        marginTop:30,
        borderRadius:7,
        padding:15
    },

    botao:{
        backgroundColor: '#F2C53D',
        padding: 15,
        borderRadius: 7,
        alignItems: 'center',
        marginTop: 20,
        marginBottom: 20
    },

    textoBotao:{
        color: '#4161BF',
        fontSize:17,
        fontWeight: 'bold'
    },

    botaoTarefa:{
        padding:15,
        marginBottom: 10
    },

    textoBotaoTarefa:{
        color: '#FFF',
        fontSize:22,
        fontWeight: 'bold'
    }
    });
